    case (x, y) if x == 0 | x == 3999 | y == 0 | y == 3999 => 1
    case _ => 0
  }

  //  val (mouse, cheese) = (Cell(1, 1), Cell(maze.length - 2, maze.head.length - 2))
  val (mouse, cheese) = (Cell(1, 1), Cell(3998, 3998))

  test("Sequence BFS Test") {
    println("================ Sequence BFS in Maze ================")
    val startTime = System.nanoTime()
    val parent = seqBFS(mouse, empty_maze)
    val endTime = System.nanoTime()
    val time = (endTime - startTime) / 1e6
    println(s"BFS time = $time s")
    //    println(s"Parents: ${parent.mkString(", ")}")
    println(s"Parent size: ${parent.size}")
  }

  test("Parallel BFS Test") {
    println("================ Parallel BFS in Maze ================")
    val startTime = System.nanoTime()
    val parent = parallelBFS(mouse, cheese, empty_maze)
    val endTime = System.nanoTime()
    val time = (endTime - startTime) / 1e6
    println(s"BFS time = $time ms")
    //    println(s"Parents: ${parent.mkString(", ")}")
    println(s"Parent size: ${parent.size}")
  }

